import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import { Col, Row, Card, Input, Button, Spin, Skeleton } from "antd";
import {
  HeartFilled,
  TeamOutlined,
  TagsOutlined,
  CommentOutlined,
} from "@ant-design/icons";

function App() {
  const [openChatList, setOpenChatList] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchOpenChatList() {
      const res = await axios.get("http://localhost:5000/getLinks", {
        headers: { "Access-Control-Allow-Origin": "*" },
      });

      setOpenChatList(res.data.result.lists);
      setLoading(false);
    }

    fetchOpenChatList();

    return () => {};
  }, []);

  function renderOpenChatList() {
    console.log(openChatList);

    return openChatList.map((chat) => {
      return (
        // <Col span={6} style={{ margin: "10px 0" }}>
        <Card
          title={chat.name}
          style={{
            backgroundColor: "#fefefa",
          }}
        >
          <p>
            <TeamOutlined /> {chat.headcount} / <HeartFilled /> {chat.like}
          </p>

          <a
            aria-label="Link to enter chat room"
            href={chat.openlink}
            target="_blank"
            rel="noreferrer"
          >
            <Button>
              <CommentOutlined /> 입장
            </Button>
          </a>
          <div>
            <small>최근 메세지: {chat.lastchat}</small>
          </div>

          <hr />
          <div>
            <TagsOutlined />{" "}
            {chat.tags.map((tag) => {
              return <span>{tag} </span>;
            })}
          </div>
        </Card>
        // </Col>
      );
    });
  }

  const { Search } = Input;

  return (
    <>
      <div class="masonry-container">
        <div class="masonry-item">
          {openChatList.map((chat) => {
            return <div>{chat.name}</div>;
          })}
          }
        </div>
      </div>

      {/* <div className="container">
        <Search placeholder="#요리 #쿡 #셰프" style={{ width: 500 }} />

        {openChatList.length > 0 ? (
          <Row gutter={16}>{renderOpenChatList()}</Row>
        ) : (
          <Skeleton active />
        )}
      </div> */}
    </>
  );
}

export default App;
