import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import { Space, Card } from "antd";
import {
  HeartFilled,
  TeamOutlined,
  TagsOutlined,
  CommentOutlined,
} from "@ant-design/icons";

function App() {
  const [openChatList, setOpenChatList] = useState([]);

  useEffect(() => {
    async function fetchOpenChatList() {
      const res = await axios.get("http://localhost:5000/getLinks", {
        headers: { "Access-Control-Allow-Origin": "*" },
      });

      // console.log(res.data.result.lists);

      setOpenChatList(res.data.result.lists);
    }

    fetchOpenChatList();

    return () => {};
  }, []);

  function renderOpenChatList() {
    console.log(openChatList);

    return openChatList.map((chat) => {
      return (
        <Card title={chat.name} style={{ width: "100%" }}>
          <img
            src={chat.wp}
            alt="chat room img"
            style={{ width: "50%", opacity: "0.5" }}
          />
          <p>
            <a
              aria-label="Link to enter chat room"
              href={chat.openlink}
              target="_blank"
              rel="noreferrer"
            >
              <CommentOutlined /> 입장
            </a>
          </p>
          <p>
            <TeamOutlined />
            {chat.headcount}
          </p>
          <p>
            <HeartFilled />
            {chat.like}
          </p>
          <hr />
          <div>
            <TagsOutlined />
            {chat.tags.map((tag) => {
              return <span>{tag} </span>;
            })}
          </div>
        </Card>
      );
    });
  }

  return <Space direction="vertical">{renderOpenChatList()}</Space>;
}

export default App;
