import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [openChatList, setOpenChatList] = useState([]);

  useEffect(() => {
    async function fetchOpenChatList() {
      const res = await axios.get("http://localhost:5000/getLinks", {
        headers: { "Access-Control-Allow-Origin": "*" },
      });

      // console.log(res.data.result.lists);

      setOpenChatList(res.data.result.lists);
    }

    fetchOpenChatList();

    return () => {};
  }, []);

  function renderOpenChatList() {
    console.log(openChatList);

    return openChatList.map((chat) => {
      return (
        <div class="card" key={chat.openlink} style={{ width: "200px" }}>
          <img src={chat.pfimg} style={{ width: "100%" }} alt="chat img" />
          <div class="container">
            <h4 key={chat.openlink}>{chat.name}</h4>
            <p>{chat.owner}</p>
            <p>Like: {chat.like}</p>
            <p>Participants: {chat.headcount}</p>
            <br />
            <a href={chat.openlink}>Link</a>
            <hr />
            {chat.tags.map((tag) => {
              return <span>{tag}</span>;
            })}
          </div>
        </div>
      );
    });
  }

  return (
    <div className="App">
      <h1>Open Chat Lists</h1>
      {renderOpenChatList()}
    </div>
  );
}

export default App;
