import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import { Col, Row, Card, Input } from "antd";
import {
  HeartFilled,
  TeamOutlined,
  TagsOutlined,
  CommentOutlined,
} from "@ant-design/icons";

function App() {
  const [openChatList, setOpenChatList] = useState([]);

  useEffect(() => {
    async function fetchOpenChatList() {
      const res = await axios.get("http://localhost:5000/getLinks", {
        headers: { "Access-Control-Allow-Origin": "*" },
      });

      setOpenChatList(res.data.result.lists);
    }

    fetchOpenChatList();

    return () => {};
  }, []);

  function renderOpenChatList() {
    console.log(openChatList);

    return openChatList.map((chat) => {
      return (
        <Col span={6} style={{ margin: "10px 0" }}>
          <Card title={chat.name}>
            <p>
              <a
                aria-label="Link to enter chat room"
                href={chat.openlink}
                target="_blank"
                rel="noreferrer"
              >
                <CommentOutlined /> 입장
              </a>
            </p>
            <p>
              <TeamOutlined /> {chat.headcount}
            </p>
            <p>최근 메세지: {chat.lastchat}</p>
            <p>
              <HeartFilled /> {chat.like}
            </p>
            <hr />
            <div>
              <TagsOutlined />
              {chat.tags.map((tag) => {
                return <span>{tag} </span>;
              })}
            </div>
          </Card>
        </Col>
      );
    });
  }

  const { Search } = Input;

  return (
    <>
      <div className="container">
        <Search placeholder="#요리 #쿡 #셰프" style={{ width: 500 }} />

        <Row gutter={16}>{renderOpenChatList()}</Row>
      </div>
      ,
    </>
  );
}

export default App;
